// Crear el componente con JavaScript
function crearComponente(titulo, descripcion, imagenURL, botonTexto, botonURL) {
    // Crear elementos HTML
    var componente = document.getElementById("miComponente");
    var tituloElement = document.createElement("h2");
    var descripcionElement = document.createElement("p");
    var imagenElement = document.createElement("img");
    var botonElement = document.createElement("a");
    botonElement.className="btn";
    
  
    // Configurar contenido y atributos
    tituloElement.textContent = titulo;
    descripcionElement.textContent = descripcion;
    imagenElement.src = imagenURL;
    imagenElement.alt = "Imagen";
    botonElement.textContent = botonTexto;
    botonElement.href = botonURL;
    botonElement.className = "boton-descarga";
  
    // Agregar elementos al componente
    componente.appendChild(tituloElement);
    componente.appendChild(imagenElement);
    componente.appendChild(descripcionElement);
    componente.appendChild(botonElement);
  }
  
