  // Función para crear un componente de libro
  function crearComponenteLibro(libro) {
    // Crear elementos HTML
    var contenedorLibros = document.getElementById("contenedorLibros");
    var componenteLibro = document.createElement("div");
    componenteLibro.className = "componente-libro"; // Añadir la clase componente-libro al div
  
    var tituloElement = document.createElement("h2");
    var descripcionElement = document.createElement("p");
    var imagenElement = document.createElement("img");
    var botonElement = document.createElement("a");
  
    // Configurar contenido y atributos
    tituloElement.textContent = libro.titulo;
    descripcionElement.textContent = libro.descripcion;
    imagenElement.src = libro.imagenURL;
    imagenElement.alt = "Imagen";
    botonElement.textContent = libro.botonTexto;
    botonElement.href = libro.botonURL;
    botonElement.className = "boton-descarga";
  
    // Agregar elementos al componente
    componenteLibro.appendChild(tituloElement);
    componenteLibro.appendChild(imagenElement);
    componenteLibro.appendChild(descripcionElement);
    componenteLibro.appendChild(botonElement);
    
    // Agregar componente de libro al contenedor
    contenedorLibros.appendChild(componenteLibro);
  }
  
  // Crear componentes de libros
  libros.forEach(function(libro) {
    crearComponenteLibro(libro);
  });
